class Q3Model:

    def __init__(self):
        self.cmodel = pickle.load(open('flaskr/q3_critic_model.sav', 'rb'))

    def predict(self, platform=[], developer=''):
        df_row = {}

        for i in range(len(DF_COLS)):
            df_row[DF_COLS[i]] = [0]

        for col in df_row:
            if col in platform or col == developer:
                df_row[col][0] = 1

        df = pd.DataFrame(df_row)

        return {
            'critic_score': self.cmodel.predict(df)[0]
        }

if __name__ == '__main__':
    print(Q3Model().predict(platform=['xbox'], developer='konami'))